INSERT INTO `soal` VALUES (1,1,1,'Berikut yang BUKAN elemen budaya adalah..',1,'Perlengkapan kehidupan manusia','mata pencaharian/sistem perekonomian manusia','sistem sosial','bahasa dan seni','regulasi dan kebijakan','E','','','','','','',''),(2,1,2,'Apa definisi budaya menurut E.B Taylor?',1,'Kompleks pengetahuan, kepercayaan, seni, moral, hukum, dan kebiasaan','Kebudayaan sebagai segala hasil karya, cita rasa, dan ciptaan masyarakat','Kebudayaan hanya mencakup seni dan kepercayaan','kebudayaan merupakan sistem hukum dan norma','kebudayaan hanya mencakup sistem perekenomian dan mata pencaharian masyarakat','A','','','','','','',''),(3,1,3,'Apa yang dimaksud dengan norma budaya?',1,'Standar perilaku yang berlaku bagi beberapa kelompok tertentu','Standar perilaku yang harus diikuti dan dipatuhi','Seperangkat aturan yang tidak berhubungan dengan perilaku masyarakat','adat istiadat yang tidak memiliki kekuatan mengikat','aturan yang tidak perlu diikuti','B','','','','','','',''),(4,1,4,'Ada lima lembaga/institusi sosial dasar dalam kehidupan bermasyarakat, berikut yang bukan termasuk lembaga dasar.',1,'Institusi keluarga','Institusi Agama','Institusi politik/pemerintah','Institusi kesehatan','Institusi pendidikan','D','','','','','','',''),(5,1,5,'Apa yang dimaksud dengan fungsi sosialisasi (Socialization Function) dalam institusi keluarga?',1,'Keluarga memberikan rasa aman dan perlindungan','Keluarga adalah tempat pertama anak belajar perilaku, komunikasi, dan cara hidup sesuai dengan budaya masyarakatnya','Keluarga menjadi sumber kenyamanan dan stabilitas emosional bagi anggotanya.','Keluarga berperan dalam mengawasi perilaku anggota-anggotanya','pemenuhan kebutuhan materi seperti makanan, tempat tinggal, pakaian, pendidikan','B','','','','','','',''),(6,1,6,'Apa yang dimaksud dengan fungsi Affective Function (Fungsi Afektif) dalam institusi keluarga?',1,'Keluarga adalah tempat pertama anak belajar perilaku, komunikasi, dan cara hidup sesuai dengan budaya masyarakat