INSERT INTO `soal` VALUES ('1459','114','4','Apa yang dimaksud dengan pinjaman subprime','1','pinjaman yang diberikan kepada peminjam dengan riwayat kredit yang sangat baik','pinjaman yang diberikan kepada peminjam tanpa memeriksa riwayat kredit','pinjaman yang diberikan kepada peminjam dengan suku bunga yang sangat rendah','pinjaman yang diberikan kepada peminjam dengan riwayat kredit yang buruk atau tidak memenuhi syarat untuk mendapatkan pinjaman dengan suku bunga yang lebih rendah.','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1460','114','5','Bagaimana pinjaman subprime mempengaruhi pasar perumahan?','1','Pinjaman subprime tidak memiliki dampak apa-apa pada pasar perumahan','Pinjaman subprime meningkatkan keamanan pasar perumahan','Pinjaman subprime meningkatkan risiko kredit dan memicu krisis keuangan.','Pinjaman subprime menurunkan risiko kredit dan memperkuat pasar perumahan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1461','114','6','Apa saja dampak negatif dari pinjaman subprime terhadap peminjam?','1','Peningkatan kepercayaan dari pihak bank','Peningkatan tingkat bunga dan risiko default','Peningkatan keuntungan bagi peminjam','Penurunan tingkat bunga dan risiko default','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1462','114','7','Bagaimana lembaga keuangan dapat mengurangi risiko pinjaman subprime?','1','Dengan memberikan pinjaman tanpa memeriksa kredit','Dengan melakukan analisis kredit yang lebih ketat, memperketat persyaratan pinjaman, dan memonitor secara ketat pembayaran pinjaman.','Dengan menawarkan suku bunga yang lebih rendah kepada peminjam subprime','Dengan tidak memeriksa kemampuan peminjam untuk membayar pinjaman','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1463','114','8','Apa yang dimaksud dengan kreditur subprime?','1','Pemberi pinjaman kepada peminjam dengan riwayat kredit yang buruk atau tidak memadai','Pemberi pinjaman kepada peminjam tanpa memeriksa riwayat kredit','Pemberi pinjaman kepada peminjam dengan riwayat kredit yang tidak relevan','Pemberi pinjaman kepada peminjam dengan riwayat kredit yang sangat baik','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1464','114','9','Bagaimana cara mengidentifikasi pinjaman subprime yang berisiko tinggi?','1','Dengan memperhatikan tingkat suku bunga yang tinggi, latar belakang kredit yang buruk, dan persyaratan pembayaran yang tidak jelas.','Dengan hanya memperhatikan latar belakang kredit yang baik dan persyaratan pembayaran yang jelas.','Dengan tidak memperhatikan tingkat suku bunga, latar belakang kredit, dan persyaratan pembayaran.','Dengan memperhatikan tingkat suku bunga yang rendah, latar belakang kredit yang baik, dan persyaratan pembayaran yang jelas','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1465','114','10','Apa saja langkah-langkah yang dapat diambil untuk mencegah terjadinya krisis pinjaman subprime di masa depan?','1','Menerapkan standar pemberian pinjaman yang lebih ketat, melakukan penilaian yang teliti terhadap peminjam, dan meningkatkan transparansi dalam proses pemberian pinjaman.','Menyembunyikan informasi terkait proses pemberian pinjaman','Memberikan pinjaman tanpa melakukan penilaian terhadap peminjam','Mengurangi suku bunga pinjaman secara drastis','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1466','114','11','Apa yang dimaksud dengan kejahatan white collar?','1','Kejahatan yang dilakukan oleh pekerja buruh','Kejahatan yang dilakukan oleh orang miskin','Kejahatan yang dilakukan oleh anak-anak muda','Kejahatan yang dilakukan oleh individu atau kelompok dengan posisi sosial atau ekonomi tinggi','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1467','114','12','Sebutkan contoh-contoh kejahatan white collar yang sering terjadi di dunia bisnis','1','Pembunuh','Pencurian Mobil','Perampokan bank','Penipuan, pencucian uang, insider trading, korupsi','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1468','114','13','Bagaimana cara para pelaku kejahatan white collar melakukan tindakan kejahatannya?','1','Menggunakan kekerasan fisik untuk mencapai tujuan','Menggunakan senjata api untuk melakukan perampokan','Menggunakan posisi dan kepercayaan untuk melakukan tindakan seperti pemalsuan, penipuan, insider trading, dan suap.','Menggunakan kekerasan verbal untuk memaksa korban','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1469','114','14','Apa yang menjadi motivasi para pelaku kejahatan white collar?','1','Keuntungan finansial atau kekuasaan','Ketidaktahuan tentang hukum','Ketidakmampuan untuk mendapatkan pekerjaan lain','Kebutuhan akan persahabatan','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1470','114','15','Apa dampak dari kejahatan white collar terhadap dunia bisnis?','1',' Kerugian finansial, kerusakan reputasi, dan hilangnya kepercayaan','Kenaikan kepercayaan dan reputasi yang baik','Peningkatan kejujuran dan etika dalam bisnis','Peningkatan keamanan finansial','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1471','114','16','Bagaimana cara pencegahan kejahatan white collar dilakukan dalam dunia bisnis?','1','Tidak memberikan pelatihan kepada karyawan dan mengabaikan kebijakan etika','Implementasi kebijakan etika, pengawasan internal yang ketat, dan pelatihan karyawan tentang tindakan yang tidak etis','Meningkatkan kecurangan dan korupsi di dalam perusahaan','Membiarkan karyawan bertindak sesuai keinginan pribadi tanpa aturan yang jelas','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1472','114','17','Bagaimana peran pemerintah dalam menangani kejahatan white collar?','1','Pemerintah memiliki peran dalam menegakkan hukum dan membuat regulasi untuk mencegah kejahatan white collar.','Pemerintah tidak perlu membuat regulasi khusus untuk mencegah kejahatan white collar','Pemerintah tidak memiliki peran dalam menangani kejahatan white collar','Pemerintah hanya fokus pada kejahatan konvensional, bukan kejahatan white collar','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1473','114','18','Apa yang dimaksud dengan kredit macet dalam konteks pinjaman subprime?','1','Pinjaman yang dilunasi tepat waktu oleh peminjam','Pinjaman yang tidak memerlukan pembayaran bunga','Pinjaman yang tidak dapat dilunasi oleh peminjam karena kondisi keuangan yang buruk','Pinjaman yang diberikan kepada peminjam dengan kondisi keuangan yang baik','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1474','114','19','Bagaimana cara mengidentifikasi pinjaman subprime yang berisiko tinggi?','1','Melakukan analisis terhadap credit score, riwayat pembayaran, dan tingkat penghasilan peminjam.','Menganalisis pinjaman KPR','Menganlisis Credit Card','Salah Semua','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1475','114','20','Apa yang dimaksud dengan pencucian uang?','1','Proses untuk menyembunyikan asal-usul uang yang diperoleh dari kegiatan legal agar terlihat ilegal','Proses untuk menggandakan uang secara ilegal','Proses untuk menyembunyikan asal-usul uang yang diperoleh dari kegiatan ilegal agar terlihat legal.','Semua salah','','C','','','','','','','');
