INSERT INTO `soal` VALUES ('1870','122','25','Manakah dari berikut yang merupakan lokasi alternatif untuk menghitung denyut nadi pada ekstremitas atas?','1','a. Arteri femoralis','b. Arteri karotis','c. Arteri brakialis','d. Arteri popliteal','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1871','122','26','Apa dua pergerakan utama yang terjadi pada manusia selama bernafas?','1','a. Kontraksi dan relaksasi otot diafragma','b. Ekspirasi dan inspirasi','c. Rotasi dan fleksi tulang belikat','d. Abduksi dan adduksi dada','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1872','122','27','Organ apa yang merupakan pusat pengendalian pernafasan dalam sistem tubuh manusia?','1','a. Jantung','b. Paru-paru','c. Otak','d. Ginjal','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1873','122','28','Di mana pertukaran gas oksigen dan karbon dioksida terjadi dalam sistem pernapasan?','1','a. Laring','b. Trakea','c. Bronkus','d. Alveolus','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1874','122','29','Apa yang dimaksud dengan difusi dalam konteks proses oksigenasi?','1','a. Pergerakan oksigen dari darah ke sel-sel tubuh','b. Perpindahan oksigen dari alveolus ke pembuluh darah kapiler','c. Perubahan karbon dioksida menjadi oksigen di paru-paru','d. Penyatuan oksigen dan karbon dioksida dalam sel-sel tubuh','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1875','122','30','Apa yang mungkin terjadi jika terjadi kegagalan dalam proses oksigenasi?','1','a. Peningkatan kesadaran','b. Hipoksia atau kekurangan oksigen di jaringan tubuh','c. Peningkatan detak jantung','d. Penurunan tekanan darah','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1876','122','31','Apa yang dimaksud dengan nyeri?','1','a. Respon tubuh terhadap rangsangan fisik','b. Perasaan tidak nyaman yang dapat dirasakan di seluruh tubuh','c. Sensasi menyenangkan di bagian tubuh tertentu','d. Reaksi emosional positif terhadap stimulus','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1877','122','32','Apa yang terjadi selama proses transduksi nyeri?','1','a. Sinyal nyeri diubah menjadi impuls elektrik','b. Impuls nyeri dikirim ke sumsum tulang belakang','c. Peningkatan produksi endorfin','d. Otot-otot di sekitar area yang nyeri berkontraksi','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1878','122','33','Apa yang menyebabkan nyeri noksius?','1','a. Kerusakan jaringan atau cedera','b. Gangguan sistem saraf pusat','c. Reaksi terhadap stimulus yang tidak menyakitkan','d. Kondisi psikologis seperti stres','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1879','122','34','Apa yang terjadi selama proses difusi?','1','a. Partikel bergerak dari area konsentrasi rendah ke area konsentrasi tinggi','b. Partikel bergerak dari area konsentrasi tinggi ke area konsentrasi rendah','c. Partikel bergerak secara acak di seluruh ruang','d. Partikel tidak bergerak sama sekali','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1880','122','35','Apa yang terjadi selama proses osmosis?','1','a. Air bergerak dari larutan yang lebih encer ke larutan yang lebih pekat','b. Air bergerak dari larutan yang lebih pekat ke larutan yang lebih encer','c. Zat-zat terlarut bergerak dari larutan yang lebih encer ke larutan yang lebih pekat','d. Tidak ada pergerakan air atau zat-zat terlarut','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1881','122','36','Apa yang menjadi prinsip dasar dalam pemberian obat pada manusia?','1','a. Pemberian obat sesuai dengan preferensi pasien','b. Dosis obat harus selalu maksimal untuk memastikan efektivitas','c. Pemberian obat harus didasarkan pada diagnosis yang tepat','d. Pemberian obat harus dilakukan tanpa mempertimbangkan riwayat kesehatan pasien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1882','122','37','Yang merupakan mekanisme perjalanan dari obat adalah','1','a.?? Absorbs, distribusi, metabolism, ekskresi','b.  Distribusi, absorbs, metabolism, eksresi','c. Absorbs, ekskresi, distribusi, metabolism','d.	Distribusi, absorbs, eksresi, metabolism','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1883','122','38','Dimana letak lokasi pemberian obat intramuskular (IM) yang umum pada lengan?','1','a. Deltoid','b. Vastus Lateralis','c. Gluteus Maximus','d. Rectus Femoris','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1884','122','39','Dimana letak lokasi pemberian obat intramuskular yang umum pada bokong?','1','a. Deltoid','b. Vastus Lateralis','c. Gluteus Maximus','d. Rectus Femoris','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1885','122','40','Bagaimana cara menemukan lokasi pemberian obat intramuskular di Gluteus Maximus?','1','a. 1/3 SIAS ke Os koksigis','b. Letakkan tangan di atas kepala dan suntikkan di area yang terasa nyaman','c. Tempatkan dua jari di atas panggul dan suntikkan obat di sebelahnya','d. Berdiri dan suntikkan obat pada area tengah lengan atas','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1886','122','41','Apa yang membedakan injeksi intravena (IV) dari jenis injeksi lainnya?','1','A. Disuntikkan ke dalam otot','B. Disuntikkan langsung ke pembuluh darah','C. Diberikan melalui kulit','D. Hanya dilakukan oleh dokter','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1887','122','42','Apa keuntungan utama dari pemberian obat melalui injeksi intravena?','1','A. Proses yang cepat dan langsung masuk ke aliran darah','B. Tidak menyakitkan','C. Tidak memerlukan keahlian khusus','D. Efek obat yang lebih lama','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1888','122','43','Apa risiko yang mungkin terjadi selama atau setelah injeksi intravena?','1','A. Ketergantungan obat','B. Infeksi pada area suntikan','C. Kehilangan selera makan','D. Penyusutan otot','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1889','122','44','Bagaimana perawatan setelah pemberian injeksi intravena yang baik?','1','A. Tidak perlu perawatan khusus','B. Membersihkan area suntikan dengan alkohol','C. Meninggalkan area suntikan tanpa perlindungan','D. Hanya mencuci dengan air biasa','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1890','122','45','Apa fungsi utama tourniquet (alat pemutus aliran darah) selama pemberian injeksi intravena?','1','A. Menghentikan pendarahan','B. Mempermudah penggunaan jarum','C. Memperpanjang efek obat','D. Memperbesar pembuluh darah','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1891','122','46','Apa karakteristik khas dari reaksi alergi terhadap injeksi intracutan?','1','A. Munculnya gejala segera setelah injeksi','B. Perubahan warna kulit di sekitar area suntikan','C. Tidak ada risiko reaksi alergi pada injeksi intracutan','D. Peningkatan tekanan darah secara mendadak','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1892','122','47','Apa tujuan dari pengambilan spesimen darah?','1','A. Mengetahui usia pasien','B. Mendiagnosis penyakit','C. Mengukur tinggi badan','D. Memberikan cairan tambahan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1893','122','48','Apa waktu terbaik untuk mengambil spesimen urine bersih untuk analisis yang akurat?','1','A. Setiap saat dalam sehari','B. Pagi hari saat bangun tidur','C. Malam hari sebelum tidur','D. Tengah hari setelah makan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1894','122','49','Apa tujuan utama dari pemeriksaan Pap smear?','1','A. Mendeteksi kanker payudara','B. Mendeteksi kanker serviks','C. Menilai fungsi hati','D. Mengukur tekanan darah','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1895','122','50','Apa tujuan dari persiapan pre operatif?','1','A. Memastikan pasien tidak pernah makan atau minum sejak lahir','B. Menyediakan waktu tambahan bagi tim medis untuk beristirahat','C. Mencegah risiko infeksi dan memastikan pasien siap secara fisik dan mental untuk operasi','D. Menentukan riasan wajah pasien sebelum masuk ruang operasi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1896','122','51','Apa yang harus dipertimbangkan dalam persiapan pre operatif bagi pasien dengan alergi obat?','1','A. Memberikan obat yang sama seperti yang biasa dikonsumsi pasien','B. Mengabaikan riwayat alergi dan memberikan obat sesuai kebijakan rumah sakit','C. Mendokumentasikan alergi obat secara lengkap dan memberikan alternatif yang aman','D. Tidak memberikan obat apa pun sebelum operasi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1897','122','52','Apa yang harus dihindari dalam perawatan luka post operatif?','1','A. Memastikan luka tetap kering sepanjang waktu','B. Menutup luka secara longgar untuk mempercepat penyembuhan','C. Menghindari penggunaan sarung tangan saat merawat luka','D. Memotong benang jahitan lebih awal dari jadwal yang ditentukan','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1898','122','53','Apa yang menjadi fokus utama perawatan pasien dalam fase post operatif?','1','A. Meningkatkan intensitas latihan fisik','B. Mencegah infeksi dan mempromosikan pemulihan','C. Memberikan obat penenang secara berlebihan','D. Menyajikan hidangan kesukaan pasien','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1899','122','54','Apa yang harus dihindari saat membersihkan luka?','1','A. Menggunakan antiseptik yang direkomendasikan','B. Menggosok luka secara kuat untuk menghilangkan kotoran','C. Menggunakan air mengalir untuk membersihkan luka','D. Membiarkan luka tetap kering dan tidak membersihkannya','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1900','122','55','Apa yang harus dilakukan jika luka mengeluarkan cairan berwarna kuning atau hijau?','1','A. Biarkan saja, karena itu adalah bagian dari proses penyembuhan','B. Bersihkan luka dengan air dan perban baru','C. Gunakan antibiotik tanpa berkonsultasi dengan profesional medis','D. Konsultasikan dengan profesional medis karena bisa menandakan infeksi','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1901','122','56','Bagaimana cara terbaik untuk memeriksa tanda-tanda infeksi pada luka?','1','A. Memeriksa warna dan ukuran plester luka','B. Mengamati adanya cairan yang keluar dari luka','C. Memeriksa suhu tubuh pasien','D. Mengganti perban secara teratur','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1902','122','57','Apa yang dimaksud dengan fase inflamasi dalam penyembuhan luka?','1','A. Terbentuknya jaringan granulasi','B. Respon awal tubuh terhadap cedera atau luka','C. Terjadi proliferasi sel-sel baru','D. Penyembuhan luka mencapai puncaknya','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1903','122','58','Apa yang terjadi pada fase proliferasi penyembuhan luka?','1','A. Pendarahan dan pembentukan bekuan darah','B. Pembentukan jaringan granulasi dan proliferasi sel-sel baru','C. Fase pembersihan dan penghilangan jaringan mati','D. Terbentuknya jaringan parut','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1904','122','59','Apa yang menjadi karakteristik luka kronis?','1','A. Sembuh dengan cepat dan tanpa bekas','B. Berlangsung lebih dari 6 minggu dan kesulitan sembuh','C. Tidak menimbulkan rasa sakit','D. Hanya terjadi pada lansia','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1905','122','60','Bagaimana klasifikasi luka berdasarkan mekanisme terjadinya?','1','A. Luka termal, luka gesekan, luka senjata tajam, dan luka tusukan','B. Luka asam, luka alkali, luka bakar, dan luka dingin','C. Luka terbuka, luka tertutup, luka infeksi, dan luka pembuluh darah','D. Luka kronis, luka akut, luka instan, dan luka tertunda','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1906','122','61','Langkah apa yang harus diambil jika seseorang tidak bernapas dan tidak memiliki denyut nadi?','1','A. Memberikan bantuan napas bantuan','B. Memberikan pijatan jantung','C. Memberikan minum','D. Menunggu bantuan medis datang','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1907','122','62','Berapa frekuensi kompresi dada yang direkomendasikan saat melakukan CPR pada orang dewasa?','1','A. Sekitar 30 kompresi per 2 napas','B. Sekitar 15 kompresi per 1 napas','C. Sekitar 5 kompresi per 1 napas','D. Sekitar 10 kompresi per 2 napas','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1908','122','63','Apa yang harus dilakukan setelah memberikan defibrilasi?','1','A. Melanjutkan CPR','B. Memberikan obat penenang','C. Memanggil keluarga pasien','D. Memberikan makanan dan minuman','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1909','122','64','Apa yang perlu dilakukan jika seseorang tiba-tiba pingsan di depan Anda?','1','A. Mengejar mereka','B. Memberikan makanan atau minuman','C. Menempatkan mereka dalam posisi duduk','D. Memeriksa respons dan memanggil bantuan medis','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1910','122','65','Apa yang harus dilakukan jika seseorang mengalami syok?','1','A. Memberikan makanan dan minuman','B. Membangunkan pasien dengan cara memanggil namanya','C. Meninggikan kaki pasien dan menjaga agar tetap hangat','D. Menunggu bantuan medis datang','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1911','122','66','Apa yang termasuk dalam langkah-langkah penilaian pasien dalam manajemen kegawatdaruratan?','1','A. Mengevaluasi kecepatan berbicara pasien','B. Memeriksa tanda-tanda vital dan kesadaran','C. Menentukan warna cat kuku pasien','D. Menanyakan pada pasien tentang riwayat penyakit keluarga','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1912','122','67','Apa yang perlu dilakukan terlebih dahulu dalam manajemen kegawatdaruratan?','1','A. Menilai situasi keselamatan','B. Memanggil bantuan medis','C. Memberikan pertolongan pertama kepada korban','D. Memeriksa identitas pasien','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1913','122','68','Apa yang dimaksud dengan \"triage\" dalam manajemen kegawatdaruratan?','1','A. Penilaian cepat dan prioritisasi tindakan berdasarkan tingkat keparahan pasien','B. Tindakan pertolongan pertama pada korban kecelakaan','C. Pemberian obat-obatan darurat kepada pasien','D. Penentuan jenis bantuan medis yang diberikan kepada pasien','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1914','122','69','Apa yang dapat dilakukan perawat untuk membantu klien menghadapi rasa takut terhadap kematian?','1','A. Membicarakan kematian secara terus terang dan tanpa sensitivitas','B. Menyediakan informasi tentang proses kematian dengan detail','C. Menyediakan dukungan emosional, mendengarkan, dan membicarakan rasa takut klien','D. Mengabaikan rasa takut klien dan fokus pada aspek fisik penyakit saja','','D','','','','','','','');
INSERT INTO `soal` VALUES ('1915','122','70','Apa yang perlu diperhatikan oleh perawat ketika merencanakan asuhan pada klien yang menghadapi kematian?','1','A. Menekankan pada aspek fisik dan medis tanpa memperhatikan aspek emosional dan spiritual','B. Mengabaikan perasaan dan harapan klien terkait kehidupan setelah kematian','C. Melibatkan keluarga dan anggota tim perawatan dalam merencanakan asuhan','D. Memutuskan sendiri keputusan terbaik tanpa melibatkan klien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1916','122','71','Apa yang dapat menjadi tanda bahwa klien mengalami tahap kemarahan dalam proses berduka?','1','A. Ketenangan dan keheningan','B. Ekspresi perasaan marah dan kekesalan','C. Menerima kenyataan tanpa perlawanan','D. Kesediaan untuk berbicara tentang kenangan bersama orang yang meninggal','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1917','122','72','Apa yang bisa dilakukan sebagai langkah awal dalam memberikan dukungan kepada klien yang menghadapi kematian seorang yang dicintai?','1','A. Memberikan saran dan solusi untuk mengatasi kesedihan','B. Menilai seberapa baik klien dapat mengendalikan emosinya','C. Mendengarkan dengan empati dan menunjukkan kehadiran yang mendukung','D. Memberikan obat penenang untuk membantu klien tidur lebih baik','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1918','122','73','Apa yang dimaksud dengan \"dual process model\" dalam asuhan pada klien yang mengalami kehilangan atau kematian?','1','A. Proses penerimaan dan penolakan secara bersamaan','B. Proses berdua antara klien dan keluarga','C. Proses penerimaan yang hanya melibatkan dua orang','D. Proses pengambilan keputusan bersama antara klien dan tim medis','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1919','122','74','Langkah terakhir dalam prosedur mencuci tangan menurut WHO adalah...','1','A. Membilas tangan dengan air bersih','B. Mengeringkan tangan dengan hand dryer','C. Menyeka tangan dengan kertas tisu','D. Menggunakan kain lap','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1920','122','75','Berapa lama waktu yang disarankan untuk menggosok tangan dengan sabun saat mencuci tangan menurut WHO?','1','A. Setidaknya 5 detik','B. Sekitar 10-15 detik','C. Lebih dari 30 detik','D. Kurang dari 3 detik','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1921','122','76','Apa yang dimaksud dengan daur ulang dalam pengelolaan sampah?','1','A. Membuang sampah ke lahan pembuangan','B. Mengolah sampah menjadi produk baru','C. Membakar sampah untuk mengurangi volume','D. Menyimpan sampah di tempat penyimpanan sementara','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1922','122','77','Seorang pasien dengan tingkat kesadaran yang menurun dan hanya dapat dibangunkan dengan rangsangan kuat mungkin memiliki kondisi kesadaran yang disebut...','1','A. Somnolens','B. Stupor','C. Koma','D. Kesadaran penuh','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1923','122','78','Jika seorang pasien tidak respons terhadap rangsangan apapun, termasuk rangsangan nyeri, kondisi kesadarannya diklasifikasikan sebagai...','1','A. Somnolens','B. Stupor','C. Koma','D. Kesadaran penuh','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1924','122','79','Tingkat kesadaran yang menunjukkan bahwa pasien responsif terhadap rangsangan, tetapi mungkin agak bingung dan motoric fleksi abnormal, biasanya memiliki skor GCS sekitar...','1','A. 3-5','B. 6-8','C. 9-12','D. 13-15','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1925','122','80','Pada skala GCS, skor tertinggi menunjukkan kondisi kesadaran yang...','1','A. Sangat baik','B. Sangat buruk','C. Tidak dapat dinilai','D. Biasa saja','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1926','122','81','Skala apa yang sering digunakan untuk menilai tingkat kesadaran pasien dan memiliki nilai antara 3 hingga 15?','1','A. Skala Glasgow Coma Scale (GCS)','B. Skala Body Mass Index (BMI)','C. Skala Blood Pressure Index (BPI)','D. Skala Visual Analog Scale (VAS)','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1927','122','82','Apa yang dapat menjadi tanda bahwa pasien mengalami hipoksia?','1','A. Kulit dan bibir berwarna pucat atau kebiruan','B. Peningkatan suhu tubuh','C. Peningkatan tekanan darah','D. Detak jantung yang melambat','','A','','','','','','','');
INSERT INTO `soal` VALUES ('1928','122','83','Manakah dari berikut yang dapat meningkatkan risiko gangguan oksigenasi pada seseorang?','1','A. Aktivitas fisik yang teratur','B. Kondisi penyakit paru-paru','C. Pemenuhan kebutuhan nutrisi yang baik','D. Terapi oksigen yang konsisten','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1929','122','84','Apa yang menjadi tujuan utama manajemen nyeri?','1','A. Menghilangkan semua rasa sakit','B. Mengurangi intensitas nyeri menjadi tingkat yang dapat ditoleransi','C. Membuat pasien tidak merasakan nyeri sama sekali','D. Memberikan obat penghilang rasa sakit secara terus menerus','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1930','122','85','Pada penggunaan skala nyeri, skala yang paling umum digunakan dan melibatkan angka 0 hingga 10 adalah...','1','A. Skala Wong-Baker','B. Skala Numerik Analog (VAS)','C. Skala McGregor','D. Skala Piktografis','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1931','122','86','Apa yang dapat menjadi tanda hipokalemia (kekurangan kalium) pada seseorang?','1','A. Hipertensi','B. Kelemahan otot dan aritmia jantung','C. Retensi cairan','D. Peningkatan suhu tubuh','','B','','','','','','','');
INSERT INTO `soal` VALUES ('1932','122','87','Apa fungsi utama cairan dalam tubuh manusia?','1','A. Memberikan energi untuk aktivitas fisik','B. Mengangkut oksigen ke sel-sel tubuh','C. Menjaga suhu tubuh dan mendukung reaksi kimia','D. Meningkatkan fungsi sistem saraf pusat','','C','','','','','','','');
INSERT INTO `soal` VALUES ('1933','122','88','Sputum yang dihasilkan oleh pasien dapat memberikan petunjuk tentang...','1','A. Kadar gula darah','B. Status hidrasi','C. Kondisi saluran pencernaan','D. Kondisi dan jenis infeksi paru-paru','','D','','','','','','','');
