INSERT INTO `soal` VALUES ('4979','166','34','Bagaimana paradigma kebidanan mengatasi tantangan akses terhadap pelayanan kesehatan reproduksi?','1','Tidak memiliki strategi untuk meningkatkan akses','Meminimalkan pelayanan di daerah pedesaan','Mengutamakan pelayanan di daerah perkotaan','Mengembangkan strategi untuk meningkatkan akses di semua lokasi','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4980','166','35','Apa yang menjadi prinsip dasar teori kebidanan?','1','Menekankan hanya pada tindakan medis','Mengabaikan keberagaman budaya dalam pelayanan','Mempertimbangkan aspek fisik, psikologis, dan sosial dalam perawatan','Tidak memiliki dasar ilmiah','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4981','166','36','Apa tujuan utama manajemen kebidanan?','1','Meningkatkan jumlah kelahiran','Mengurangi biaya layanan kesehatan','Meningkatkan kesejahteraan ibu hamil','Menjalankan praktik kebidanan tanpa perubahan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4982','166','37','Apa tujuan utama dokumentasi kebidanan?','1','Memenuhi persyaratan administratif saja','Memberikan informasi kepada pihak rumah sakit','Meningkatkan kualitas pelayanan dan keamanan pasien','Menyusun catatan medis hanya untuk keperluan internal','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4983','166','38','Dokumentasi kebidanan yang baik melibatkan:','1','Menulis catatan hanya pada akhir shift','Catatan yang bersifat subjektif dan tidak rinci','Pencatatan waktu yang tidak sesuai dengan kejadian sebenarnya','Catatan yang akurat, jelas, dan terperinci','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4984','166','39','Dalam dokumentasi kebidanan, apa yang dimaksud dengan singkatan \"SOAP\"?','1','Sebuah metode untuk membersihkan tangan','Subjektif, Objektif, Analisis, dan Rencana asuhan','Sistem Organisasi Analisis Proses','Standar Operasional Analisis Pasien','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4985','166','40','Mengapa dokumentasi waktu sangat penting dalam catatan kebidanan?','1','Tidak memiliki dampak pada evaluasi pasien','Penting untuk menentukan gaji/ imbalan kepada bidan','Memastikan akurasi dan kronologis catatan','Tidak ada konsekuensi hukum terkait dengan catatan waktu','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4986','166','41','Apa fungsi utama catatan perkembangan kebidanan?','1','Menghindari tanggung jawab hukum','Menyimpan informasi rahasia tentang pasien','Menyediakan rekam medis yang komprehensif dan terus-menerus','Menunjukkan peran dominan petugas Kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4987','166','42','Dalam membuat catatan kebidanan, mengapa penting untuk mencatat rencana perawatan?','1','Hanya sebagai formalitas administratif','Untuk memberikan gambaran umum tentang kondisi pasien','Agar bidan dapat mengikuti rencana dengan konsisten','Tidak memiliki dampak pada perawatan pasien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4988','166','43','Apa yang termasuk dalam bagian \"Objektif\" dari catatan SOAP?','1','Perasaan dan pengalaman pasien','Pengamatan dan pemeriksaan fisik yang terukur','Penilaian subjektif petugas kesehatan','Rencana perawatan yang direkomendasikan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4989','166','44','Apa yang dimaksud dengan \"informasi subjektif\" dalam catatan kebidanan?','1','Fakta yang dapat diukur dan diamati secara langsung','Informasi yang diberikan oleh pasien atau keluarga tentang pengalaman atau perasaan mereka','Data klinis yang diperoleh dari pemeriksaan fisik','Catatan waktu dan durasi setiap kejadian','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4990','166','45','Mengapa penting untuk mencatat informasi tentang persetujuan pasien dalam catatan kebidanan?','1','Hanya sebagai formalitas administratif','Untuk memenuhi kebutuhan pihak rumah sakit','Menunjukkan bahwa pasien telah memberikan persetujuan terhadap perawatan yang diberikan','Tidak memiliki dampak pada hubungan pasien dengan petugas Kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4991','166','46','Bagaimana penggunaan teknologi informasi dapat meningkatkan dokumentasi kebidanan?','1','Mengurangi akurasi dan aksesibilitas catatan','Memperlambat proses pencatatan','Meningkatkan efisiensi, keamanan, dan aksesibilitas informasi','Tidak memiliki dampak pada pelayanan kesehatan reproduksi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4992','166','47','Apa yang dapat dilakukan untuk meningkatkan keamanan dan privasi dokumentasi kebidanan?','1','Membagikan informasi pasien dengan pihak yang tidak berwenang','Menjaga kerahasiaan informasi pasien dan memberikan akses hanya kepada personel yang berwenang','Tidak melakukan penguncian perangkat yang berisi informasi pasien','Mengabaikan prinsip privasi untuk kepentingan pelayanan Kesehatan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4993','166','48','Tujuan SDGs yang paling terkait dengan peran bidan adalah:','1','Tujuan 1: Mengakhiri Kemiskinan','Tujuan 3: Kesehatan dan Kesejahteraan ','Tujuan 7: Energi Terjangkau dan Bersih','Tujuan 12: Konsumsi dan Produksi Bertanggung Jawab','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4994','166','49','Apa peran utama bidan dalam mencapai Tujuan Pembangunan Berkelanjutan (SDGs)?','1','Menyediakan bantuan medis darurat','Meningkatkan keberlanjutan lingkungan','Memastikan kesehatan reproduksi dan maternal yang baik','Mengelola proyek infrastruktur local','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4995','166','50','Bagaimana peran bidan dapat membantu mencapai Tujuan 2: Memberantas Kelaparan?','1','Mengelola program pendidikan','Memberikan konsultasi gizi kepada ibu hamil','Mengelola proyek pertanian','Memberikan vaksinasi kepada anak-anak','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4996','166','51','Bagaimana bidan dapat mendukung Tujuan 4: Pendidikan Berkualitas?','1','Menjadi guru di sekolah lokal','Memberikan penyuluhan kesehatan reproduksi di sekolah','Membangun fasilitas pendidikan baru','Mengelola program keuangan untuk siswa','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4997','166','52','Apa dampak positif dari peran bidan terhadap Tujuan 16: Perdamaian, Keadilan, dan Institusi yang Kuat?','1','Menurunkan tingkat keamanan di komunitas','Mempromosikan perdamaian dengan memberikan pelayanan kesehatan yang merata','Menetapkan hukum dan peraturan yang membatasi akses ke layanan kesehatan','Tidak memiliki dampak pada situasi keamanan dan keadilan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4998','166','53','Apa peran bidan dalam mendukung Tujuan 9: Industri, Inovasi, dan Infrastruktur?','1','Mengelola proyek konstruksi infrastruktur','Mendorong inovasi di bidang teknologi kesehatan','Menolak memberikan layanan kepada pekerja industri','Tidak memiliki dampak pada sektor industri dan infrastruktur','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4999','166','54','Apa tujuan pemberian reward (penghargaan) kepada bidan?','1','Menghukum kesalahan bidan','Meningkatkan motivasi dan kinerja bidan','Menekan inovasi dalam praktik kebidanan','Membuat bidan lebih takut pada atasan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5000','166','55',' Apa dampak positif yang dapat diberikan oleh pemberian hadiah kepada bidan yang berprestasi?','1','Menurunkan motivasi bidan','Meningkatkan kepercayaan pasien pada bidan','Menekan inovasi dan kreativitas','Meningkatkan semangat kerja dan kepuasan kerja bidan ','','D','','','','','','','');
INSERT INTO `soal` VALUES ('5001','166','56','Punishment (hukuman) yang efektif dalam kebidanan harus:','1','Bersifat fisik dan keras','Mengakibatkan pemecatan langsung','Bersifat proporsional dan mendidik','Dilakukan secara sembunyi-sembunyi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5002','166','57','Mengapa penting untuk menghindari hukuman yang bersifat merendahkan atau mempermalukan bagi bidan?','1','Untuk meningkatkan semangat kerja','Agar bidan tidak merasa diabaikan','Untuk mencegah penurunan semangat dan ketidakpuasan kerja','Meningkatkan efektivitas komunikasi','','C','','','','','','','');
