INSERT INTO `soal` VALUES ('5004','166','59',' Apa manfaat memberikan pengakuan atau apresiasi secara publik kepada bidan yang berprestasi?','1','Menurunkan semangat kerja','Meningkatkan motivasi dan kepercayaan diri','Menghambat komunikasi tim','Meminimalkan kolaborasi antarbidan','','B','','','','','','','');
