INSERT INTO `soal` VALUES ('5006','166','61','Reward yang bersifat non-finansial dapat mencakup:','1','Peningkatan gaji','Pujian dan apresiasi','Pemindahan ke tempat yang lebih nyaman','Pengurangan waktu kerja','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5007','166','62','Reward yang bersifat jangka panjang dapat mencakup:','1','Bonus kinerja bulanan','Promosi ke jabatan yang lebih tinggi','Pujian verbal tanpa imbalan finansial','Sertifikat penghargaan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5008','166','63','Punishment yang bersifat mendidik harus:','1','Menghukum secara publik untuk efek jera','Meningkatkan keterlibatan bidan dalam pembelajaran','Menyembunyikan alasan di balik hukuman','Tidak memberikan kesempatan untuk perbaikan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5009','166','64','Apa dampak pemberian hukuman yang tidak adil terhadap motivasi dan kinerja bidan?','1','Meningkatkan semangat kerja','Menurunkan kepercayaan diri','Meningkatkan inovasi','Meningkatkan kolaborasi ','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5010','166','65','Bagaimana pemberian punishment yang terlalu berat dapat memengaruhi citra PMB, rumah sakit atau institusi kebidanan?','1','Meningkatkan reputasi positif','Menurunkan kepercayaan masyarakat','Memperkuat kepuasan pasien','Membuat institusi lebih dihormati','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5011','166','66','Mengapa penting bagi kebijakan hadiah dan hukuman di tempat kerja untuk adil dan transparan?','1','Untuk meningkatkan kepuasan pasien','Agar bidan merasa dihargai dan diperlakukan secara adil','Untuk menekan inovasi dan kreativitas','Meningkatkan persaingan antar bidan','','B','','','','','','','');
