INSERT INTO `soal` VALUES ('5013','166','68','Apa yang dimaksud dengan pengembangan profesional dalam konteks pendidikan kebidanan?','1','Tidak ada perluasan pengetahuan dan keterampilan setelah lulus','Mematikan perkembangan karir setelah lulus','Kontinu merawat tanpa pembaharuan pengetahuan','Proses berkelanjutan untuk meningkatkan pengetahuan dan keterampilan bidan','','D','','','','','','','');
INSERT INTO `soal` VALUES ('5014','166','69','Pengembangan karir bidan dapat mencakup partisipasi dalam pelatihan dan seminar untuk:','1','Menjalin hubungan sosial saja','Mendapatkan cuti kerja tambahan','Up to date keilmuan dalam praktik kebidanan','Hanya untuk memenuhi persyaratan pekerjaan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5015','166','70','Apakah sertifikasi resmi diperlukan untuk pengembangan karir bidan?','1','Tidak diperlukan karena pengalaman kerja cukup','Ya, sertifikasi resmi sangat penting','Hanya jika bidan ingin bekerja di luar negeri','Tidak memiliki dampak pada pengembangan karir','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5016','166','71','Apakah pengembangan karir bidan dapat mencakup penulisan artikel atau buku tentang pengalaman praktiknya?','1','Tidak relevan untuk pengembangan karir','Hanya untuk bidan peneliti','Ya, sebagai bentuk berbagi pengetahuan','Hanya jika diperintahkan oleh atasan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5017','166','72','Bagaimana bidan dapat memanfaatkan jejaring sosial untuk pengembangan karir?','1','Hanya untuk keperluan pribadi','Berpartisipasi dalam diskusi dan berbagi pengetahuan','Berpartisipasi dalam diskusi dan berbagi pengetahuan','Tidak memiliki dampak pada pengembangan karir','','C','','','','','','','');
