INSERT INTO `soal` VALUES ('5019','166','74','Model praktik kebidanan yang menempatkan keluarga sebagai fokus utama dalam perawatan disebut:','1','Model Kebidanan Tradisional','Model Keluarga Berpusat','Model Biopsikososial','Model Kolaboratif','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5020','166','75','Karakteristik utama dari Model Keluarga Berpusat adalah:','1','Fokus pada keputusan ibu hamil','Pemisahan peran antara anggota keluarga','Keluarga sebagai mitra dalam perawatan','Tidak memberikan ruang bagi tradisi dan kepercayaan keluarga','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5021','166','76','Apa yang dimaksud dengan evidence-based kebidanan?','1','Praktik kebidanan berdasarkan tradisi','Informasi dan tindakan kebidanan berdasarkan bukti ilmiah ','Melakukan tindakan tanpa dasar ilmiah','Mengandalkan pengalaman pribadi tanpa mengacu pada literatur','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5022','166','77','Mengapa evidence-based kebidanan penting dalam praktik klinis?','1','Karena mengikuti tren populer','Agar memudahkan pekerjaan bidan','Untuk memberikan perawatan yang efektif dan aman','Karena merupakan persyaratan hukum','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5023','166','78','Mengapa penting bagi bidan untuk mengakses dan membaca literatur ilmiah?','1','Hanya untuk memenuhi persyaratan formal','Agar tetap terkini dengan tren mode praktik','Untuk mendukung informasi dan tindakan kebidanan dengan bukti ilmiah','Tidak perlu karena pengalaman praktis lebih penting','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5024','166','79','Bagaimana seorang bidan dapat memastikan bahwa bukti ilmiah yang digunakan relevan dengan kasus pasien tertentu?','1','Mengabaikan informasi pasien','Menilai kondisi pasien secara holistik','Tidak melakukan evaluasi terhadap literatur ilmiah','Mengikuti pedoman umum tanpa pertimbangan spesifik pasien','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5025','166','80','Apa yang dimaksud dengan informed consent dalam evidence-based kebidanan?','1','Mengabaikan hak pasien untuk mengetahui','Menerima persetujuan tanpa memberikan informasi','Memberikan informasi lengkap kepada pasien untuk mendukung keputusan mereka','Tidak memberikan pilihan kepada pasien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5026','166','81','Apa yang dapat dilakukan bidan untuk terus mengikuti perkembangan bukti ilmiah dalam kebidanan?','1','Menolak untuk membaca literatur ilmiah','Mengikuti hanya opini ahli tanpa merujuk pada penelitian','Melakukan pelatihan berkelanjutan dan mengikuti konferensi','Mengandalkan pengalaman pribadi tanpa pertimbangan bukti ilmiah','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5027','166','82','Apa dampak positif dari penerapan evidence-based kebidanan pada hasil kesehatan maternal dan neonatal?','1','Meningkatkan risiko komplikasi','Menurunkan kualitas perawatan','Meningkatkan keselamatan ibu dan bayi','Tidak memiliki dampak pada hasil kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5028','166','83','Bagaimana peran pasien dalam penerapan evidence-based kebidanan?','1','Hanya sebagai objek pasif dalam perawatan','Tidak memiliki peran dalam membuat keputusan','Terlibat dalam pengambilan keputusan dan menerima informasi','Tidak berhak mengetahui bukti ilmiah','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5029','166','84','Apa konsep dasar dari evidence-based kebidanan?','1','Menolak penggunaan bukti ilmiah','Mengutamakan pengalaman pribadi','Mengintegrasikan bukti ilmiah dalam praktik kebidanan','Memisahkan bukti ilmiah dari praktik klinis','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5030','166','85','Bagaimana evaluasi bukti ilmiah yang digunakan dalam kebidanan dapat meningkatkan kualitas asuhan?','1','Tidak memiliki dampak pada kualitas perawatan','Dengan mengandalkan pengalaman pribadi','Dengan memilih bukti ilmiah yang tidak relevan','Dengan mengevaluasi secara kritis dan memperbarui praktik berdasarkan bukti ilmiah','','D','','','','','','','');
INSERT INTO `soal` VALUES ('5031','166','86','Apa yang menjadi fokus utama midwifery update terkini?','1','Peningkatan keamanan prosedur medis','Inovasi dalam pendekatan perawatan keluarga','Perubahan dalam kurikulum pelatihan bidan','Pemisahan peran antara bidan dan dukun bayi','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5032','166','87','Apa peran teknologi informasi dalam midwifery update?','1','Mengurangi keterlibatan teknologi informasi','Meningkatkan akurasi pemantauan dan rekam medis','Menolak penggunaan teknologi informasi','Menggantikan peran bidan dengan sistem otomatis','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5033','166','88','Apa dampak midwifery update terhadap kolaborasi antara bidan dan tenaga medis lainnya?','1','Memperkuat batas antara bidan dan tenaga medis','Meningkatkan kerjasama dan pemahaman bersama','Menekankan keunggulan satu kelompok profesi','Menciptakan ketidaksetaraan dalam tugas','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5034','166','89','Bagaimana midwifery update merespons perkembangan teknologi medis dalam kebidanan?','1','Mengurangi keterlibatan teknologi medis','Menggunakan teknologi untuk meningkatkan keamanan','Menolak penggunaan teknologi medis secara keseluruhan','Memisahkan teknologi dari praktik kebidanan ','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5035','166','90','Bagaimana midwifery update memperhatikan masalah kesejahteraan mental ibu hamil?','1','Mengabaikan kesejahteraan mental sebagai prioritas','Menekankan pentingnya dukungan emosional dan kesejahteraan mental','mental','Memisahkan kesejahteraan mental dari praktik kebidanan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5036','166','91','Apa yang dimaksud dengan pemasaran sosial dalam konteks jasa asuhan kebidanan?','1','Promosi produk-produk kesehatan','Upaya memasarkan kebidanan secara komersial','Menggunakan teknologi dalam praktik kebidanan','Penerapan strategi pemasaran dalam praktik kebidanan ','','D','','','','','','','');
INSERT INTO `soal` VALUES ('5037','166','92','Apa tujuan utama dari pemasaran sosial jasa asuhan kebidanan?','1','Meningkatkan penjualan layanan kebidanan','Meningkatkan keuntungan finansial','Meningkatkan kesadaran masyarakat akan kebutuhan asuhan kebidanan','Meminimalkan biaya pelayanan kebidanan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5038','166','93','Manfaat apa yang dapat diperoleh dari penerapan pemasaran sosial jasa asuhan kebidanan?','1','Peningkatan tarif layanan kebidanan','Penurunan kepercayaan masyarakat pada bidan','Peningkatan kualitas pelayanan dan kesadaran masyarakat','Pengurangan jumlah pelayanan kebidanan yang disediakan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('5039','166','94','Apa peran kesadaran masyarakat dalam pemasaran sosial jasa asuhan kebidanan?','1','Mengurangi permintaan terhadap layanan kebidanan','Meningkatkan kepercayaan masyarakat pada bidan','Mengurangi keterlibatan masyarakat dalam layanan kebidanan','Meningkatkan biaya layanan kebidanan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5040','166','95','Bagaimana teknologi dapat digunakan dalam pemasaran sosial jasa asuhan kebidanan?','1','Menolak penggunaan teknologi dalam praktik kebidanan','Meningkatkan akses informasi dan edukasi melalui media sosial','Menyulitkan akses masyarakat terhadap layanan kebidanan','Mengurangi interaksi antara bidan dan klien','','B','','','','','','','');
INSERT INTO `soal` VALUES ('5041','166','96','Apa peran media sosial dalam pemasaran sosial jasa asuhan kebidanan?','1','Mengurangi akses informasi masyarakat','Menolak penggunaan media sosial dalam praktik kebidanan','Meningkatkan interaksi dan edukasi masyarakat terkait kebidanan','Menjaga kerahasiaan informasi layanan kebidanan','','C','','','','','','','');
