INSERT INTO `soal` VALUES ('4760','165','15','Bagaimana paradigma kebidanan mencerminkan pendekatan berbasis bukti dalam praktik klinis?','1','Bergantung pada kepercayaan tradisional','Menolak bukti ilmiah','Mengutamakan penelitian dan bukti ilmiah','Hanya mengandalkan pengalaman pribadi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4761','165','16','Apa peran teknologi dan informasi dalam mendukung paradigma kebidanan?','1','Tidak memiliki dampak signifikan','Menurunkan kualitas pelayanan','Meningkatkan akses dan kualitas pelayanan','Membatasi peran bidan dalam pelayanan kebidanan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4762','165','17','Bagaimana paradigma kebidanan mencerminkan prinsip-prinsip hak asasi manusia dalam pelayanan kesehatan reproduksi?','1','Mengabaikan hak asasi manusia','Mempertahankan tradisi tanpa memperhatikan hak-hak individu','Menjunjung tinggi hak-hak individu dan keadilan sosial','Tidak memiliki dampak pada hak asasi manusia','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4763','165','18','Apa dampak perubahan paradigma kebidanan terhadap hubungan antara bidan dan pasien?','1','Menurunkan keterlibatan pasien dalam pengambilan keputusan','Meningkatkan kepercayaan dan keterlibatan antara bidan dan pasien','Tidak ada dampak pada hubungan antara bidan dan pasien','Mengurangi peran pasien dalam perawatan Kesehatan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4764','165','19','Apa peran etika dalam paradigma kebidanan?','1','Tidak memiliki dampak pada etika','Hanya fokus pada kebutuhan ibu hamil','Menjunjung tinggi prinsip-prinsip etika dalam pelayanan kesehatan reproduksi','Mencampur adukkan etika dengan praktik medis','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4765','165','20','Apa yang menjadi fokus utama profesi kebidanan?','1','Penyakit menular','Kesehatan ibu dan anak','Kesehatan jiwa','Kesehatan gigi','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4766','165','21','Apa yang menjadi peran utama bidan selama kehamilan?','1','Memberikan obat-obatan untuk mengurangi nyeri','Melakukan tindakan operasi caesar','Memberikan dukungan fisik dan emosional kepada ibu','Menyelidiki penyakit menular','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4767','165','22','Bagaimana peran bidan dalam pelayanan kesehatan reproduksi remaja?','1','Tidak terlibat dalam pelayanan untuk remaja','Memberikan edukasi kesehatan reproduksi dan pelayanan konseling','Hanya fokus pada persalinan','Menyediakan pelayanan untuk kelompok usia dewasa','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4768','165','23','Apa yang diakui sebagai etika dalam praktik kebidanan?','1','Menolak hak pasien untuk mengambil keputusan','Mengabaikan rahasia medis','Mempertahankan privasi dan kerahasiaan pasien','Melakukan tindakan medis tanpa persetujuan pasien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4769','165','24','Apa dampak perkembangan teknologi terhadap praktik kebidanan?','1','Menurunkan kualitas pelayanan','Meningkatkan aksesibilitas dan keamanan pelayanan','Tidak ada dampak signifikan','Mengurangi peran bidan dalam pelayanan Kesehatan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4770','165','25','Apa yang menjadi fokus utama pelayanan kebidanan di daerah pedesaan?','1','Menyediakan pelayanan eksklusif untuk ibu hamil','Meningkatkan jumlah tindakan medis','Memberikan akses terhadap pelayanan kesehatan reproduksi','Tidak ada perbedaan fokus pelayanan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4771','165','26','Apa yang diakui sebagai dampak positif pendekatan keluarga berpusat dalam praktik kebidanan?','1','Meningkatkan biaya pelayanan','Menurunkan partisipasi keluarga dalam keputusan kesehatan','Meningkatkan kepercayaan dan dukungan keluarga','Tidak memiliki dampak pada praktek kebidanan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4772','165','27','Bagaimana keberagaman budaya dapat memengaruhi praktik kebidanan?','1','Tidak ada pengaruh budaya dalam praktik kebidanan','Mempertahankan praktik standar tanpa mempertimbangkan budaya','Menghormati dan memahami nilai-nilai budaya dalam perawatan','Hanya melayani kelompok etnis tertentu','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4773','165','28','Apa yang diakui sebagai faktor utama dalam meningkatkan kualitas pendidikan bidan?','1','Menurunkan standar pendidikan','Meningkatkan jumlah lembaga pendidikan bidan','Memberikan dukungan dan sumber daya yang memadai','Tidak melakukan perubahan pada pendidikan bidan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4774','165','29','Apa yang diakui sebagai peran bidan dalam mencegah dan mengatasi komplikasi kehamilan?','1','Hanya memberikan perawatan pada kasus darurat','Menyediakan obat-obatan untuk mengatasi komplikasi','Melakukan tindakan operasi caesar','Mengidentifikasi dini dan merujuk kehamilan risiko tinggi','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4775','165','30','Apa yang diakui sebagai peran bidan dalam mendukung pemberdayaan perempuan?','1','Hanya memberikan perawatan medis','Mempertahankan tradisi tanpa memperhatikan hak-hak individu','Menjunjung tinggi hak-hak individu dan memberikan dukungan keputusan','Tidak memiliki dampak pada pemberdayaan perempuan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4776','165','31','Bagaimana peran bidan dalam mendukung kesehatan reproduksi di komunitas?','1','Hanya memberikan perawatan individu','Menjadi isolatif terhadap komunitas','Mengutamakan kolaborasi dan partisipasi masyarakat','Tidak memberikan perhatian pada kebutuhan keluarga','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4777','165','32','Apa yang menjadi peran bidan dalam meningkatkan aksesibilitas pelayanan kesehatan reproduksi?','1','Mengabaikan pelayanan di daerah pedesaan','Menurunkan kualitas pelayanan di puskesmas','Meningkatkan layanan di rumah sakit saja','Meningkatkan kualitas pelayanan di puskesmas','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4778','165','33','Bagaimana peran bidan dalam mendukung peningkatan partisipasi keluarga dalam pengambilan keputusan kesehatan reproduksi?','1','Tidak memperhatikan peran keluarga dalam keputusan kesehatan','Menekankan hanya pada keputusan ibu hamil','Meningkatkan partisipasi keluarga dalam pengambilan keputusan','Tidak ada dampak peran keluarga dalam keputusan Kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4779','165','34','Bagaimana paradigma kebidanan mengatasi tantangan akses terhadap pelayanan kesehatan reproduksi?','1','Tidak memiliki strategi untuk meningkatkan akses','Meminimalkan pelayanan di daerah pedesaan','Mengutamakan pelayanan di daerah perkotaan','Mengembangkan strategi untuk meningkatkan akses di semua lokasi','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4780','165','35','Apa yang menjadi prinsip dasar teori kebidanan?','1','Menekankan hanya pada tindakan medis','Mengabaikan keberagaman budaya dalam pelayanan','Mempertimbangkan aspek fisik, psikologis, dan sosial dalam perawatan','Tidak memiliki dasar ilmiah','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4781','165','36','Apa tujuan utama manajemen kebidanan?','1','Meningkatkan jumlah kelahiran','Mengurangi biaya layanan kesehatan','Meningkatkan kesejahteraan ibu hamil','Menjalankan praktik kebidanan tanpa perubahan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4782','165','37','Apa tujuan utama dokumentasi kebidanan?','1','Memenuhi persyaratan administratif saja','Memberikan informasi kepada pihak rumah sakit','Meningkatkan kualitas pelayanan dan keamanan pasien','Menyusun catatan medis hanya untuk keperluan internal','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4783','165','38','Dokumentasi kebidanan yang baik melibatkan:','1','Menulis catatan hanya pada akhir shift','Catatan yang bersifat subjektif dan tidak rinci','Pencatatan waktu yang tidak sesuai dengan kejadian sebenarnya','Catatan yang akurat, jelas, dan terperinci','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4784','165','39','Dalam dokumentasi kebidanan, apa yang dimaksud dengan singkatan \"SOAP\"?','1','Sebuah metode untuk membersihkan tangan','Subjektif, Objektif, Analisis, dan Rencana asuhan','Sistem Organisasi Analisis Proses','Standar Operasional Analisis Pasien','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4785','165','40','Mengapa dokumentasi waktu sangat penting dalam catatan kebidanan?','1','Tidak memiliki dampak pada evaluasi pasien','Penting untuk menentukan gaji/ imbalan kepada bidan','Memastikan akurasi dan kronologis catatan','Tidak ada konsekuensi hukum terkait dengan catatan waktu','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4786','165','41','Apa fungsi utama catatan perkembangan kebidanan?','1','Menghindari tanggung jawab hukum','Menyimpan informasi rahasia tentang pasien','Menyediakan rekam medis yang komprehensif dan terus-menerus','Menunjukkan peran dominan petugas Kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4787','165','42','Dalam membuat catatan kebidanan, mengapa penting untuk mencatat rencana perawatan?','1','Hanya sebagai formalitas administratif','Untuk memberikan gambaran umum tentang kondisi pasien','Agar bidan dapat mengikuti rencana dengan konsisten','Tidak memiliki dampak pada perawatan pasien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4788','165','43','Apa yang termasuk dalam bagian \"Objektif\" dari catatan SOAP?','1','Perasaan dan pengalaman pasien','Pengamatan dan pemeriksaan fisik yang terukur','Penilaian subjektif petugas kesehatan','Rencana perawatan yang direkomendasikan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4789','165','44','Apa yang dimaksud dengan \"informasi subjektif\" dalam catatan kebidanan?','1','Fakta yang dapat diukur dan diamati secara langsung','Informasi yang diberikan oleh pasien atau keluarga tentang pengalaman atau perasaan mereka','Data klinis yang diperoleh dari pemeriksaan fisik','Catatan waktu dan durasi setiap kejadian','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4790','165','45','Mengapa penting untuk mencatat informasi tentang persetujuan pasien dalam catatan kebidanan?','1','Hanya sebagai formalitas administratif','Untuk memenuhi kebutuhan pihak rumah sakit','Menunjukkan bahwa pasien telah memberikan persetujuan terhadap perawatan yang diberikan','Tidak memiliki dampak pada hubungan pasien dengan petugas Kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4791','165','46','Bagaimana penggunaan teknologi informasi dapat meningkatkan dokumentasi kebidanan?','1','Mengurangi akurasi dan aksesibilitas catatan','Memperlambat proses pencatatan','Meningkatkan efisiensi, keamanan, dan aksesibilitas informasi','Tidak memiliki dampak pada pelayanan kesehatan reproduksi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4792','165','47','Apa yang dapat dilakukan untuk meningkatkan keamanan dan privasi dokumentasi kebidanan?','1','Membagikan informasi pasien dengan pihak yang tidak berwenang','Menjaga kerahasiaan informasi pasien dan memberikan akses hanya kepada personel yang berwenang','Tidak melakukan penguncian perangkat yang berisi informasi pasien','Mengabaikan prinsip privasi untuk kepentingan pelayanan Kesehatan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4793','165','48','Tujuan SDGs yang paling terkait dengan peran bidan adalah:','1','Tujuan 1: Mengakhiri Kemiskinan','Tujuan 3: Kesehatan dan Kesejahteraan ','Tujuan 7: Energi Terjangkau dan Bersih','Tujuan 12: Konsumsi dan Produksi Bertanggung Jawab','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4794','165','49','Apa peran utama bidan dalam mencapai Tujuan Pembangunan Berkelanjutan (SDGs)?','1','Menyediakan bantuan medis darurat','Meningkatkan keberlanjutan lingkungan','Memastikan kesehatan reproduksi dan maternal yang baik','Mengelola proyek infrastruktur local','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4795','165','50','Bagaimana peran bidan dapat membantu mencapai Tujuan 2: Memberantas Kelaparan?','1','Mengelola program pendidikan','Memberikan konsultasi gizi kepada ibu hamil','Mengelola proyek pertanian','Memberikan vaksinasi kepada anak-anak','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4796','165','51','Bagaimana bidan dapat mendukung Tujuan 4: Pendidikan Berkualitas?','1','Menjadi guru di sekolah lokal','Memberikan penyuluhan kesehatan reproduksi di sekolah','Membangun fasilitas pendidikan baru','Mengelola program keuangan untuk siswa','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4797','165','52','Apa dampak positif dari peran bidan terhadap Tujuan 16: Perdamaian, Keadilan, dan Institusi yang Kuat?','1','Menurunkan tingkat keamanan di komunitas','Mempromosikan perdamaian dengan memberikan pelayanan kesehatan yang merata','Menetapkan hukum dan peraturan yang membatasi akses ke layanan kesehatan','Tidak memiliki dampak pada situasi keamanan dan keadilan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4798','165','53','Apa peran bidan dalam mendukung Tujuan 9: Industri, Inovasi, dan Infrastruktur?','1','Mengelola proyek konstruksi infrastruktur','Mendorong inovasi di bidang teknologi kesehatan','Menolak memberikan layanan kepada pekerja industri','Tidak memiliki dampak pada sektor industri dan infrastruktur','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4799','165','54','Apa tujuan pemberian reward (penghargaan) kepada bidan?','1','Menghukum kesalahan bidan','Meningkatkan motivasi dan kinerja bidan','Menekan inovasi dalam praktik kebidanan','Membuat bidan lebih takut pada atasan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4800','165','55',' Apa dampak positif yang dapat diberikan oleh pemberian hadiah kepada bidan yang berprestasi?','1','Menurunkan motivasi bidan','Meningkatkan kepercayaan pasien pada bidan','Menekan inovasi dan kreativitas','Meningkatkan semangat kerja dan kepuasan kerja bidan ','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4801','165','56','Punishment (hukuman) yang efektif dalam kebidanan harus:','1','Bersifat fisik dan keras','Mengakibatkan pemecatan langsung','Bersifat proporsional dan mendidik','Dilakukan secara sembunyi-sembunyi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4802','165','57','Mengapa penting untuk menghindari hukuman yang bersifat merendahkan atau mempermalukan bagi bidan?','1','Untuk meningkatkan semangat kerja','Agar bidan tidak merasa diabaikan','Untuk mencegah penurunan semangat dan ketidakpuasan kerja','Meningkatkan efektivitas komunikasi','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4803','165','58','Apa dampak dari pemberian punishment yang tidak adil?','1','Meningkatkan kepercayaan bidan terhadap manajemen','Mengurangi kepatuhan bidan terhadap aturan','Meningkatkan motivasi dan semangat kerja','Membuat bidan lebih suka bekerja di tempat tersebut','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4804','165','59',' Apa manfaat memberikan pengakuan atau apresiasi secara publik kepada bidan yang berprestasi?','1','Menurunkan semangat kerja','Meningkatkan motivasi dan kepercayaan diri','Menghambat komunikasi tim','Meminimalkan kolaborasi antarbidan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4805','165','60','Reward yang bersifat finansial dapat mencakup:','1','Bonus kinerja','Pemindahan ke lokasi kerja','Peningkatan tanggung jawab ','Pemotongan gaji ','','A','','','','','','','');
INSERT INTO `soal` VALUES ('4806','165','61','Reward yang bersifat non-finansial dapat mencakup:','1','Peningkatan gaji','Pujian dan apresiasi','Pemindahan ke tempat yang lebih nyaman','Pengurangan waktu kerja','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4807','165','62','Reward yang bersifat jangka panjang dapat mencakup:','1','Bonus kinerja bulanan','Promosi ke jabatan yang lebih tinggi','Pujian verbal tanpa imbalan finansial','Sertifikat penghargaan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4808','165','63','Punishment yang bersifat mendidik harus:','1','Menghukum secara publik untuk efek jera','Meningkatkan keterlibatan bidan dalam pembelajaran','Menyembunyikan alasan di balik hukuman','Tidak memberikan kesempatan untuk perbaikan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4809','165','64','Apa dampak pemberian hukuman yang tidak adil terhadap motivasi dan kinerja bidan?','1','Meningkatkan semangat kerja','Menurunkan kepercayaan diri','Meningkatkan inovasi','Meningkatkan kolaborasi ','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4810','165','65','Bagaimana pemberian punishment yang terlalu berat dapat memengaruhi citra PMB, rumah sakit atau institusi kebidanan?','1','Meningkatkan reputasi positif','Menurunkan kepercayaan masyarakat','Memperkuat kepuasan pasien','Membuat institusi lebih dihormati','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4811','165','66','Mengapa penting bagi kebijakan hadiah dan hukuman di tempat kerja untuk adil dan transparan?','1','Untuk meningkatkan kepuasan pasien','Agar bidan merasa dihargai dan diperlakukan secara adil','Untuk menekan inovasi dan kreativitas','Meningkatkan persaingan antar bidan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4812','165','67','Bagaimana pemberian hadiah dan hukuman dapat menjadi alat pengembangan profesional bagi bidan?','1','Dengan menghambat peluang pengembangan','Dengan meningkatkan rasa tanggung jawab','Dengan mengekang motivasi untuk belajar','Dengan mengabaikan pengembangan karir','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4813','165','68','Apa yang dimaksud dengan pengembangan profesional dalam konteks pendidikan kebidanan?','1','Tidak ada perluasan pengetahuan dan keterampilan setelah lulus','Mematikan perkembangan karir setelah lulus','Kontinu merawat tanpa pembaharuan pengetahuan','Proses berkelanjutan untuk meningkatkan pengetahuan dan keterampilan bidan','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4814','165','69','Pengembangan karir bidan dapat mencakup partisipasi dalam pelatihan dan seminar untuk:','1','Menjalin hubungan sosial saja','Mendapatkan cuti kerja tambahan','Up to date keilmuan dalam praktik kebidanan','Hanya untuk memenuhi persyaratan pekerjaan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4815','165','70','Apakah sertifikasi resmi diperlukan untuk pengembangan karir bidan?','1','Tidak diperlukan karena pengalaman kerja cukup','Ya, sertifikasi resmi sangat penting','Hanya jika bidan ingin bekerja di luar negeri','Tidak memiliki dampak pada pengembangan karir','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4816','165','71','Apakah pengembangan karir bidan dapat mencakup penulisan artikel atau buku tentang pengalaman praktiknya?','1','Tidak relevan untuk pengembangan karir','Hanya untuk bidan peneliti','Ya, sebagai bentuk berbagi pengetahuan','Hanya jika diperintahkan oleh atasan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4817','165','72','Bagaimana bidan dapat memanfaatkan jejaring sosial untuk pengembangan karir?','1','Hanya untuk keperluan pribadi','Berpartisipasi dalam diskusi dan berbagi pengetahuan','Berpartisipasi dalam diskusi dan berbagi pengetahuan','Tidak memiliki dampak pada pengembangan karir','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4818','165','73','Apa yang dapat bidan lakukan untuk mengidentifikasi peluang pengembangan karir?','1','Hanya menunggu atasan memberikan arahan','Mengikuti instruksi tanpa inisiatif','Menyusun rencana pengembangan karir pribadi','Hanya jika ada promosi yang tersedia','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4819','165','74','Model praktik kebidanan yang menempatkan keluarga sebagai fokus utama dalam perawatan disebut:','1','Model Kebidanan Tradisional','Model Keluarga Berpusat','Model Biopsikososial','Model Kolaboratif','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4820','165','75','Karakteristik utama dari Model Keluarga Berpusat adalah:','1','Fokus pada keputusan ibu hamil','Pemisahan peran antara anggota keluarga','Keluarga sebagai mitra dalam perawatan','Tidak memberikan ruang bagi tradisi dan kepercayaan keluarga','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4821','165','76','Apa yang dimaksud dengan evidence-based kebidanan?','1','Praktik kebidanan berdasarkan tradisi','Informasi dan tindakan kebidanan berdasarkan bukti ilmiah ','Melakukan tindakan tanpa dasar ilmiah','Mengandalkan pengalaman pribadi tanpa mengacu pada literatur','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4822','165','77','Mengapa evidence-based kebidanan penting dalam praktik klinis?','1','Karena mengikuti tren populer','Agar memudahkan pekerjaan bidan','Untuk memberikan perawatan yang efektif dan aman','Karena merupakan persyaratan hukum','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4823','165','78','Mengapa penting bagi bidan untuk mengakses dan membaca literatur ilmiah?','1','Hanya untuk memenuhi persyaratan formal','Agar tetap terkini dengan tren mode praktik','Untuk mendukung informasi dan tindakan kebidanan dengan bukti ilmiah','Tidak perlu karena pengalaman praktis lebih penting','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4824','165','79','Bagaimana seorang bidan dapat memastikan bahwa bukti ilmiah yang digunakan relevan dengan kasus pasien tertentu?','1','Mengabaikan informasi pasien','Menilai kondisi pasien secara holistik','Tidak melakukan evaluasi terhadap literatur ilmiah','Mengikuti pedoman umum tanpa pertimbangan spesifik pasien','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4825','165','80','Apa yang dimaksud dengan informed consent dalam evidence-based kebidanan?','1','Mengabaikan hak pasien untuk mengetahui','Menerima persetujuan tanpa memberikan informasi','Memberikan informasi lengkap kepada pasien untuk mendukung keputusan mereka','Tidak memberikan pilihan kepada pasien','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4826','165','81','Apa yang dapat dilakukan bidan untuk terus mengikuti perkembangan bukti ilmiah dalam kebidanan?','1','Menolak untuk membaca literatur ilmiah','Mengikuti hanya opini ahli tanpa merujuk pada penelitian','Melakukan pelatihan berkelanjutan dan mengikuti konferensi','Mengandalkan pengalaman pribadi tanpa pertimbangan bukti ilmiah','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4827','165','82','Apa dampak positif dari penerapan evidence-based kebidanan pada hasil kesehatan maternal dan neonatal?','1','Meningkatkan risiko komplikasi','Menurunkan kualitas perawatan','Meningkatkan keselamatan ibu dan bayi','Tidak memiliki dampak pada hasil kesehatan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4828','165','83','Bagaimana peran pasien dalam penerapan evidence-based kebidanan?','1','Hanya sebagai objek pasif dalam perawatan','Tidak memiliki peran dalam membuat keputusan','Terlibat dalam pengambilan keputusan dan menerima informasi','Tidak berhak mengetahui bukti ilmiah','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4829','165','84','Apa konsep dasar dari evidence-based kebidanan?','1','Menolak penggunaan bukti ilmiah','Mengutamakan pengalaman pribadi','Mengintegrasikan bukti ilmiah dalam praktik kebidanan','Memisahkan bukti ilmiah dari praktik klinis','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4830','165','85','Bagaimana evaluasi bukti ilmiah yang digunakan dalam kebidanan dapat meningkatkan kualitas asuhan?','1','Tidak memiliki dampak pada kualitas perawatan','Dengan mengandalkan pengalaman pribadi','Dengan memilih bukti ilmiah yang tidak relevan','Dengan mengevaluasi secara kritis dan memperbarui praktik berdasarkan bukti ilmiah','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4831','165','86','Apa yang menjadi fokus utama midwifery update terkini?','1','Peningkatan keamanan prosedur medis','Inovasi dalam pendekatan perawatan keluarga','Perubahan dalam kurikulum pelatihan bidan','Pemisahan peran antara bidan dan dukun bayi','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4832','165','87','Apa peran teknologi informasi dalam midwifery update?','1','Mengurangi keterlibatan teknologi informasi','Meningkatkan akurasi pemantauan dan rekam medis','Menolak penggunaan teknologi informasi','Menggantikan peran bidan dengan sistem otomatis','','B','','','','','','','');
