INSERT INTO `soal` VALUES ('4836','165','91','Apa yang dimaksud dengan pemasaran sosial dalam konteks jasa asuhan kebidanan?','1','Promosi produk-produk kesehatan','Upaya memasarkan kebidanan secara komersial','Menggunakan teknologi dalam praktik kebidanan','Penerapan strategi pemasaran dalam praktik kebidanan ','','D','','','','','','','');
INSERT INTO `soal` VALUES ('4837','165','92','Apa tujuan utama dari pemasaran sosial jasa asuhan kebidanan?','1','Meningkatkan penjualan layanan kebidanan','Meningkatkan keuntungan finansial','Meningkatkan kesadaran masyarakat akan kebutuhan asuhan kebidanan','Meminimalkan biaya pelayanan kebidanan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4838','165','93','Manfaat apa yang dapat diperoleh dari penerapan pemasaran sosial jasa asuhan kebidanan?','1','Peningkatan tarif layanan kebidanan','Penurunan kepercayaan masyarakat pada bidan','Peningkatan kualitas pelayanan dan kesadaran masyarakat','Pengurangan jumlah pelayanan kebidanan yang disediakan','','C','','','','','','','');
INSERT INTO `soal` VALUES ('4839','165','94','Apa peran kesadaran masyarakat dalam pemasaran sosial jasa asuhan kebidanan?','1','Mengurangi permintaan terhadap layanan kebidanan','Meningkatkan kepercayaan masyarakat pada bidan','Mengurangi keterlibatan masyarakat dalam layanan kebidanan','Meningkatkan biaya layanan kebidanan','','B','','','','','','','');
INSERT INTO `soal` VALUES ('4840','165','95','Bagaimana teknologi dapat digunakan dalam pemasaran sosial jasa asuhan kebidanan?','1','Menolak penggunaan teknologi dalam praktik kebidanan','Meningkatkan akses informasi dan edukasi melalui media sosial','Menyulitkan akses masyarakat terhadap layanan kebidanan','Mengurangi interaksi antara bidan dan klien','','B','','','','','','','');
