INSERT INTO `soal` VALUES ('4843','165','98','Apa yang dapat dianggap sebagai strategi pemasaran yang efektif dalam konteks jasa asuhan kebidanan? ','1','Menjaga informasi terkait kebidanan secara rahasia','Mengurangi transparansi dan interaksi dengan masyarakat','Menyediakan layanan kebidanan tanpa memperhatikan preferensi klien','Menggunakan pendekatan personal dan memahami kebutuhan klien ','','D','','','','','','','');
