INSERT INTO `soal` VALUES ('4947','166','2','Apa yang menjadi tugas utama seorang bidan?','1','Menyediakan dukungan emosional saja','Memberikan layanan kesehatan kepada ibu dan bayi selama kehamilan, persalinan, dan pasca persalinan','Hanya memberikan vaksinasi kepada bayi','Tidak ada peran khusus','','B','','','','','','','');
